INSERT INTO `House` (`ID`, `name`, `description`, `picture`) VALUES
(1, 'Gryffondor', 'C\'est l\'une des quatre maison de l\'école Poudlard. Créer par Godric Gryffondor. Le fantôme qui lui est lié est Sir Nicholas de Mimsy-Porpington alias « Nick Quasi-Sans-Tête ».\r\n\r\nLes qualités requises pour pouvoir être accepté dans cette maison sont : \r\n   - Courage\r\n   - Hardiesse\r\n   - Force d\'esprit', 'Gryffindor.jpeg'),
